mapper.select:
		    select 
             *
             from es_collect_shop a
		     where 1=1

			    and  a.id=#{id}


			    and  a.shopid=#{shopid}


			    and  a.userid=#{userid}

			order by collecttime desc

mapper.delete:
delete  from es_collect_shop
where  1=1

   and id =#{id}


   and  a.userid=#{userid}

mapper.select:
		    select 
             *
             from es_collect_product a
		     where 1=1

			    and  a.id=#{id}


			    and  a.productid=#{productid}


			    and  a.userid=#{userid}

			order by collecttime desc

mapper.delete:
delete  from es_collect_product
where  1=1

   and id =#{id}


   and  a.userid=#{userid}
