mapper.select:
		    select 
             a.*
             from v_eshop_shop  a
             where 1=1

			    and  a.shopid=#{shopid}


			    and  a.shopcode=#{shopcode}


			   and  a.shopuser=#{shopuser}


			   and  a.shopstate=#{shopstate}


			   and  (a.shopstate=#{searchword})

			order by shopdeveloptime desc
