mapper.select:
     	select s.*,a.areacodename,
     	(select c.industryname from es_trade_companyindustry c where s.companyindustry=c.id) industryname
     	from es_trade_companyshop s join wqt_areacode a on s.areacode=a.areacode
     	where 1= 1

	 		and s.id = #{id}


	 		and s.userid = #{userid}


	 		and s.shopcode = #{shopcode}


	 		and s.companytruename = #{companytruename}


	 		and s.lawername = #{lawername}


	 		and s.companyindustry = #{companyindustry}


	 		and s.regcapital = #{regcapital}


	 		and s.creditcode = #{creditcode}

	 	order by id desc

     	select * from es_trade_companyshop
     	where 1= 1

	 		and id = #{id}


	 		and shopcode = #{shopcode}


	 		and companytruename = #{companytruename}


	 		and lawername = #{lawername}


	 		and companyindustry = #{companyindustry}


	 		and regcapital = #{regcapital}


	 		and creditcode = #{creditcode}

mapper.update:
update es_trade_companyshop
shopcode=#{shopcode}, userid=#{userid}, companyname=#{companyname}, companytruename=#{companytruename}, companyindustry=#{companyindustry}, holddate=#{holddate}, establishdate=#{establishdate}, dutytype=#{dutytype}, regcapital=#{regcapital}, regcapitalmoney=#{regcapitalmoney}, truecapital=#{truecapital}, creditcode=#{creditcode}, companytype=#{companytype}, provinceid=#{provinceid}, provincename=#{provincename}, cityid=#{cityid}, cityname=#{cityname}, areaid=#{areaid}, areaname=#{areaname}, areacode=#{areacode}, bankaccount=#{bankaccount}, applybill=#{applybill}, netbank=#{netbank}, price=#{price}, operaterange=#{operaterange}, lawername=#{lawername}, debtissue=#{debtissue}, rightsissue=#{rightsissue}, businessissue=#{businessissue}, lawissue=#{lawissue}, dutyissue=#{dutyissue}, tangiblecapital=#{tangiblecapital}, intangiblecapital=#{intangiblecapital}, loanissue=#{loanissue}, connector=#{connector}, address=#{address}, mobileno=#{mobileno}, state=#{state}, pathkey=#{pathkey}, htmlpath=#{htmlpath}, developtime=#{developtime}, workerid=#{workerid}, remark=#{remark}, createtime=#{createtime}, bookmark=#{bookmark},
where id = #{id}

mapper.delete:
delete from es_trade_companyshop
where 1=1

	and id = #{id}
